CREATE TABLE IF NOT EXISTS `wp_abcinfosend` (
`id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
`thumb_media_id`  varchar(255) NOT NULL  COMMENT '缩约图id',
`sort`  int(10) NOT NULL  DEFAULT 100 COMMENT '排序值',
`token`  varchar(255) NOT NULL  COMMENT '公众号标识',
`author`  varchar(255) NOT NULL  DEFAULT '和蔼的木Q技术支持' COMMENT '内容作者',
`title`  varchar(255) NOT NULL  COMMENT '标题',
`content_source_url`  varchar(255) NOT NULL  COMMENT '信息内容外链接',
`content`  text NOT NULL  COMMENT '内容',
`digest`  text NOT NULL  COMMENT '简短描述',
`show_cover_pic`  char(10) NOT NULL  DEFAULT 1 COMMENT '是否显示封面',
`thumbimg`  int(10) UNSIGNED NOT NULL  COMMENT '缩略图',
PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci CHECKSUM=0 ROW_FORMAT=DYNAMIC DELAY_KEY_WRITE=0;
