CREATE TABLE IF NOT EXISTS `wp_eo2o_payment` (
`id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
`transaction_id`  varchar(255) NOT NULL  COMMENT '微信支付订单号',
`out_trade_no`  varchar(255) NOT NULL  COMMENT '商户订单号',
`attach`  varchar(255) NOT NULL  COMMENT '商家数据包',
`token`  varchar(255) NOT NULL  COMMENT 'TOKEN',
`time_end`  varchar(255) NOT NULL  COMMENT '支付完成时间',
`total_fee`  varchar(255) NOT NULL  COMMENT '总金额',
`openid`  varchar(255) NOT NULL  COMMENT '用户标识',
`err_code`  varchar(255) NOT NULL  COMMENT '错误代码',
`err_code_des`  varchar(255) NOT NULL  COMMENT '错误代码描述',
`result_code`  varchar(255) NOT NULL  COMMENT '业务结果',
`mch_id`  varchar(255) NOT NULL  COMMENT '商户号',
`appid`  varchar(255) NOT NULL  COMMENT '公众账号ID',
PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci CHECKSUM=0 ROW_FORMAT=DYNAMIC DELAY_KEY_WRITE=0;
UPDATE `wp_attribute` SET model_id= (SELECT MAX(id) FROM `wp_model`) WHERE model_id=0;